import React, { Component, useContext } from "react";
import { PlayerContext } from "../data/PlayerContext";
import axios from "axios";
export default class AddPlayer extends Component {
  //  declare state with required properties
  constructor() {
    super();
    this.state = {
      name: "",
      country: "",
      matchesPlayer: "",
    };
  }

  submitHandler(e) {
    e.preventDefault();

    const [player, setPlayer] = useContext(PlayerContext);

    const newPlayer = { name: this.name, country: this.country };
    setPlayer([]);
    // declare and initialise players ,setPlayers from context
    // use axios.post to send new player data to json api
    // and store the same to context also using setPlayer

    // reset the text fields and show acknowledgement
  }
  render() {
    return (
      <div>
        <form
          onSubmit={(e) => {
            this.submitHandler(e);
          }}
        >
          <div class="form-group">
            <lable for="name">Player Name</lable>
            <input
              className="form-control input-field"
              id="name"
              value={this.state.name}
              onChange={(e) => this.setState({ name: e.target.value })}
            />
          </div>
          <div class="form-group">
            <lable for="country">Country</lable>
            <input
              className="form-control input-field"
              id="country"
              value={this.state.country}
              onChange={(e) => this.setState({ country: e.target.value })}
            />
          </div>
          <div class="form-group">
            <lable for="name">Matches Played</lable>
            <input
              className="form-control input-field"
              value={this.state.matchesPlayer}
              onChange={(e) => this.setState({ matchesPlayer: e.target.value })}
            />
          </div>
          <button type="submit" value="submit" className="btn btn-dark">
            Submit
          </button>
        </form>
        {/* display form to accept name,country and matchesPlayed  of a player */}
      </div>
    );
  }
}
AddPlayer.contextType = PlayerContext;
