import React, { useContext, useEffect } from "react";
import { PlayerContext } from "../data/PlayerContext";
export default function ShowPlayer() {
  // initialise the player info using useContext hook
  const [players, setPlayer] = useContext(PlayerContext);

  useEffect(() => {
    fetch("http://localhost:3333/players")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPlayers(data);
        return data;
      })
      .catch((err) => {
        console.log("Unable to fetch");
      });
  }, []);

  return (
    <div className="data-table">
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Player Id</th>
            <th scope="col">Name</th>
            <th scope="col">Country</th>
            <th scope="col">Total Match Played</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player) => (
            <tr>
              <th scope="row">{player.id}</th>
              <td>{player.name}</td>
              <td>{player.country}</td>
              <td>{player.matchesPlayed}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
